const Task = (props) => {

    console.log(props.currentTaskId)


    const taskElements= props.tasks.map((task,index) =>(
        <div className="task-container">

            <h1 className='taskId' name="id">{task.id}</h1>
            <div className= 'tick' ></div>
            <TextareaAutosize 
            name="body" 
            className="task" 
            placeholder='Task'
            onClick={() => props.setCurrentTaskId(task.id)} 
            onChange={props.updateTask}
            value={task.body}
            />
        </div>
    ))

  return (
    <div>
