    state={
        isLightTheme : true,
        light:{ mode: 'light',syntax : '#555', ui:'#ddd', bg: '#eee'},
        dark:{mode :' dark',syntax : '#ddd', ui:'#333', bg: '#555'},

    }
    changeTheme = this.changeTheme.bind(this);

    changeTheme() {

        const newTheme = !this.state.isLightTheme
        const newState ={
            ...this.state, 
            newTheme
        }
        // this.state.isLightTheme=!this.state.isLightTheme;
        console.log(newTheme)


    }






    render(){
        return(
            <ThemeContext.Provider value={{...this.state, changeTheme :this.changeTheme}}>
                {this.props.children}
            </ThemeContext.Provider>
        );
    }
}
