import React from 'react'
import Task from '../task/Task'
import {data} from "../../data.js"


const Main = () => {
  // window.localStorage.clear();
  // localStorage.setItem("tasks",JSON.stringify(data));

  const [tasks, setTasks]= React.useState(() => JSON.parse(localStorage.getItem("tasks")) || []);
  const [currentTaskId, setCurrentTaskId] = React.useState(
    (tasks[0] && tasks[0].id) || "")

React.useEffect(() => {
  localStorage.setItem("tasks", JSON.stringify(tasks))
}, [tasks])

  function updateTask(event) {

    setTasks(oldTasks => oldTasks.map(oldTask =>{
      return oldTask.id===currentTaskId ?
      {... oldTask , 
      body : event.target.value} : oldTask


    }))

}
  function updateTicked(){
    console.log(currentTaskId)
    setTasks(oldTasks => oldTasks.map(oldTask =>{

      return oldTask.id===currentTaskId?
      {... oldTask,
        ticked : (!tasks[currentTaskId].ticked) } : oldTask
    }))

  }

function findCurrentTask() {
  return tasks.find(task => {
      return task.id === currentTaskId
  }) || tasks[0]


}






  return (
    <div>

        <Task
        tasks={tasks}
        updateTask={updateTask}
        setCurrentTaskId={setCurrentTaskId}
        currentTaskId={currentTaskId}
        currentTask= {findCurrentTask()}
        updateTicked={updateTicked}


        />

    </div>
  )
}

export default Main
