import React from 'react'
import "./task.css"
import TextareaAutosize from 'react-textarea-autosize';


const Task = (props) => {

    const[currentTask, setCurrentTask]= React.useState(props);
    const isEmpty=true;

    console.log(currentTask)



    function handleChange(event){

        setCurrentTask(prevCurrentTask =>{
            return {
                ...prevCurrentTask,
                [event.target.name]:event.target.value
            }
        })

    }

  return (
    <div>
        <div className="task-container">
            <h1 className='taskId' name="id">{currentTask.id}</h1>
            <div className={currentTask.checked ? 'tick' : 'untick'}></div>

            <textarea 
            name="body" 
            row="10" 
            className="task" 
            placeholder='Task 1' 
            onChange={handleChange}
            value={currentTask.body}
            />

        </div>
