import React from 'react'
import "./task.css"
import TextareaAutosize from 'react-textarea-autosize';


const Task = (props) => {


    const taskElements= props.tasks.map((task,index) =>(
        <div className="task-container">

            <h1 className='taskId' name="id">{task.id}</h1>
            <div
             className= {task.ticked ?'tick' : "untick"}
             name="ticked"
             onClick={()=>{console.log(task.id); props.updateTicked(task.id)}}
            ></div>
            <div>
                <TextareaAutosize 
                name="body" 
                className="task" 
                placeholder='Task'
                onClick={() => (props.setCurrentTaskId(task.id),console.log(props.currentTaskId))
                } 
                onChange={props.updateTask}
                value={task.body}
                />
            </div>
        </div>
    ))

  return (
    <div>
        <div>
            {taskElements}
        </div>

