  function updateTask(event){
    setTasks(oldTasks => 
      oldTasks.map(oldTask =>{
        return oldTask.id===task.id ?
          {...oldTask , body : event.target.value} : oldTask
      } ))
  }

  return (
    <div>

      {tasks ? tasks.map(task => 
        <Task
