import React from 'react'
import "./task.css"

const Task = (props) => {

    const[ currentTask, setCurrentTask]= React.useState({taskId:"", body:"",checked:false});

    console.log(currentTask);
    function handleChange(event){

        setCurrentTask(prevCurrentTask =>{
            return {
                ...prevCurrentTask,
                [event.target.name]:event.target.value
            }
        })

    }

  return (
    <div>
        <div className="task-container">
            <h1 className='taskId' name="id">{taskId}</h1>
            <div className='tick'></div>
            <input name="body" type="text" className="task" placeholder='Task 1' onChange={handleChange}/>
        </div>
    </div>
  )
}

export default Task
