import React from 'react'
import "./task.css"

const Task = (props) => {

    const[currentTask, setCurrentTask]= React.useState(props);
    const isEmpty=true;

    console.log(currentTask)

    function auto_grow(element) {
        element.style.height = "5px";
        element.style.height = (element.scrollHeight)+"px";
    }

    function handleChange(event){

        setCurrentTask(prevCurrentTask =>{
            return {
                ...prevCurrentTask,
                [event.target.name]:event.target.value
            }
        })

    }

  return (
    <div>
        <div className="task-container">
            <h1 className='taskId' name="id">{currentTask.id}</h1>
            <div className={currentTask.checked ? 'tick' : 'untick'}></div>
            <textarea 
            name="body" 
            row="10" 
            className="task" 
            placeholder='Task 1' 
            onChange={handleChange}
            value={currentTask.body}
            oninput={auto_grow(this)}/>

        </div>
        {/* {isEmpty &&
        <div className="task-container">
            <h1 className='taskId' name="id">{parseInt(currentTask.taskId) + 1 }</h1>
            <div className='tick'></div>
            <input name="body" type="text" className="task" placeholder='New Task' onChange={handleChange}/>
        </div> } */}

    </div>
  )
}

export default Task
