import React, {createContext, Component, useState, useReducer, useEffect} from "react";

export const ThemeContext = createContext();




class ThemeContextProvider extends Component{
    static theme = localStorage.getItem("theme")
    state={
        isLightTheme : this.theme || true,
        light:{ mode: 'light',syntax : '#555', ui:'#ddd', bg: '#eee'},
        dark:{mode :'dark',syntax : 'white', ui:'#333', bg: '#555'},

    }

    changeTheme=()=> {

        this.setState({
            isLightTheme : !this.state.isLightTheme
        })

        localStorage.setItem("theme", (this.state.isLightTheme))


        console.log(localStorage.getItem("theme"))




    }



    render(){
        return(
            <ThemeContext.Provider value={{...this.state, changeTheme :this.changeTheme}}>
                {this.props.children}
            </ThemeContext.Provider>
        );
    }
}
